function menu() {
	if ( $('nav').hasClass('visible') ) {
		$('nav').removeClass('visible');
		$('.nav-trigger').removeClass('active');
	} else {
		$('nav').addClass('visible');
		$('.nav-trigger').addClass('active');
	}
}

function toggleModal(id) {
	$overly = $('.overly');

	if ( id != undefined ) {
		var $modal = $(id);

		if ( $overly.hasClass('visible') ) {
			$overly.find('.box').hide();
			$modal.show();
		} else {
			$overly.addClass('visible').fadeIn('fast', function() {
				$modal.show();
			});
		}
	} else {
		$overly.removeClass('visible').fadeOut('fast', function() {
			$overly.find('.box').hide();
		});
	}
}

function tabs() {
	var $tabsNav = $(".tabs-nav a"),
			$tabs = $(".tab");

	$tabsNav.first().addClass('active');
	$tabs.hide().first().show();

	$tabsNav.click(function(e) {
		e.preventDefault();

		var $target = $( $(this).attr('href') );

		$tabsNav.removeClass('active');
		$(this).addClass('active');

		$tabs.hide();
		$target.fadeIn('fast');
	})
}

function expandables() {
	var $expandables = $(".expandable");

	$expandables.each(function() {
		var $this = $(this);

		$this.find('.expandable-content').hide();

		$this.find('.expandable-trigger').click(function(e) {
			e.preventDefault();

			if ( $(this).hasClass('active') ) {
				$(this).removeClass('active');
				$(this).find('i').removeClass('fa-minus').addClass('fa-plus');
			} else {
				$(this).addClass('active');
				$(this).find('i').removeClass('fa-plus').addClass('fa-minus');
			}

			$this.find('.expandable-content').slideToggle();
		});
	});
}

function rebuildCustomSelect(el) {
	customSelects(el);
}
function customSelects(el) {
  var $selectWrappers = el != undefined ? $(el) : $('.select-wrapper');

  if( el != undefined ) {
  	$selectWrappers.find('.select').remove();
  }

  $selectWrappers.each(function() {
    var $this = $(this),
        $nativeSelect = $(this).find('select'),
        selectOptions = [];
        label = "";


    if ( !$this.hasClass('pick-first-val') ) {
    	$nativeSelect.val('');
    	label = $nativeSelect.data("label");
    } else {
    	label = $nativeSelect.find('option').first().text();
    }

    $nativeSelect.find('option').each(function() {
    	var optionArray = [ $(this).text(), $(this).val()];

    	selectOptions.push( optionArray );
    });

    var $select = createSelect(label, selectOptions, $this);
    var $indicator = $select.find('.indicator i');

    $this.append($select);

    $select.each(function() {
    	var $this = $(this);

    	$this.click(function(e) {
    		e.stopPropagation();

    		if ( !$this.hasClass('active') ) {
    			$('body').find('.select').removeClass('active').find('.options').hide();
    		}

				$this.find('.options').fadeToggle('fast');
				$this.toggleClass('active');
    	});

	    var $options = $(this).find('.option'),
	    		$visor = $(this).find('.visor');

	    $options.click(function(e) {
	    	e.stopPropagation();

	    	if ( !$(this).hasClass('selected') ) {
		    	$options.removeClass('selected');
		    	$(this).addClass('selected');

		    	$nativeSelect.val( $(this).data("value") );
		    	$visor.text( $(this).text() );
		    	$nativeSelect.trigger('change');
		    	$this.find('.options').hide();
	    	}
	    });
    });
  });

  function createSelect(label, options, thisSelectWrapper) {
		var $thisSelectWrapper = thisSelectWrapper;
				$select = $("<div class='select' />"),
				$options = $("<div class='options' />")
				.css({
					height: 200,
					overflowY: 'auto',
					zIndex: 99
				}),
				$scrollInner = $('<div class="scrollbar-inner" />'),
	      $loader = $("<div class='loader' />")
	      .css({
		    	position: 'absolute',
		    	top: 0,
		    	left: 0,
		    	width: '100%',
		    	height: '100%',
		    	zIndex: 99,
		    	backgroundColor: 'rgba(255,255,255,.95)',
		    	backgroundImage: 'url("data:image/gif;base64,")',
		    	backgroundPosition: 'center',
		    	backgroundRepeat: 'no-repeat',
		    	display: 'none'
	    	});

		$loader.click(function(e) {
			e.stopPropagation();
		});

		$select.append("<div class='visor'>"+ label +"</div>");
		$select.append("<span class='indicator'><i class='fa fa-angle-down'></i></span>");

		for (var i = 0; i <= options.length - 1; i++) {
			var label = options[i][0],
					val = options[i][1];

			if (i == 0 && $thisSelectWrapper.hasClass('pick-first-val') ) {
				var option = $("<div />").attr({
					class: "option selected",
					"data-value": val
				}).text(label);
			} else {
				var option = $("<div />").attr({
					class: "option",
					"data-value": val
				}).text(label);
			}

			$scrollInner.append(option);
		};
		$options.append($scrollInner);
		$select.append($options);

		if ($select.find('.loader').length > 0) {
			$select.find('.loader').remove();
		}

		$select.append($loader);

		$options.hide();

		return $select;
  }
}
